INSERT INTO `spellmissilemotion` VALUES ('35', 'Glaive', 'local spinRate = 360\r\nlocal angle = 90\r\nlocal maxMagnitude = startDistance * .1\r\n\r\nmodelYaw = 90\r\nmodelRoll = time * spinRate\r\nmodelPitch = 90\r\n\r\ntransAngle = angle\r\ntransMag = (progress * 2) - 1\r\ntransMag = (1 - (transMag * transMag)) * maxMagnitude\r\n', '0', '1');
