INSERT INTO `mailtemplatedbc` VALUES ('171', 'The Argent Dawn needs your help!', 'L\'Aube d\'argent a besoin de vous !', '$N$B$BWith all haste, please open and heed the attached letter.  The Argent Dawn needs your help!', '$N$B$BS’il vous plaît, ouvrez la lettre jointe et lisez-la dès que possible. L’Aube d’argent a besoin de votre aide !');
