INSERT INTO `factiondbc` VALUES ('20', '20', '1100', '690', '1', '0', '0', '0', '0', '0', '3100', '-12000', '4000', '0', '1', '1', '3', '0', '10', '1', '0.25', '7', '5', 'Stormwind', 'Hurlevent', 'One of the last bastions of human power, this Alliance capital is ruled by the prodigal king, Varian Wrynn.', 'Cette capitale de l’Alliance est l’un des derniers bastions de la puissance des humains. Elle est gouvernée par le roi Varian Wrynn, revenu après une disparition mystérieuse.');
INSERT INTO `factiondbc` VALUES ('21', '21', '1093', '690', '8', '0', '0', '0', '0', '0', '3100', '-12000', '4000', '0', '1', '1', '3', '0', '10', '1', '0.25', '7', '5', 'Darnassus', 'Darnassus', 'This Alliance capital is populated by Night Elves and is located in the Island of Teldrassil.  Ruled by the Priestess of the Moon, Tyrande Whisperwind.', 'Située sur l’île de Teldrassil, cette capitale de l’Alliance est la demeure des elfes de la nuit. Elle est dirigée par la prêtresse de la Lune, Tyrande Murmevent.');
INSERT INTO `factiondbc` VALUES ('22', '22', '1097', '690', '4', '0', '0', '0', '0', '0', '3100', '-12000', '4000', '0', '1', '1', '3', '0', '10', '1', '0.25', '7', '5', 'Ironforge', 'Forgefer', 'This Alliance capital is the pride of the Dun Morogh dwarves.  Home to King Magni.', 'Résidence du roi Magni, cette capitale de l’Alliance fait la fierté des nains de Dun Morogh.');
INSERT INTO `factiondbc` VALUES ('23', '23', '1037', '690', '64', '0', '0', '0', '0', '0', '3100', '-12000', '4000', '0', '1', '1', '3', '0', '10', '1', '0.25', '7', '5', 'Gnomeregan Exiles', 'Exilés de Gnomeregan', 'The former denizens of Gnomeregan, now living in exile in Ironforge and in league with the Alliance.', 'Les anciens habitants de Gnomeregan vivent en exil à Forgefer et se sont associés à l’Alliance.');
INSERT INTO `factiondbc` VALUES ('24', '24', '77', '946', '1024', '0', '0', '0', '0', '0', '3000', '-12000', '4000', '0', '1', '1', '3', '0', '10', '1', '0.25', '7', '5', 'Exodar', 'Exodar', 'The naaru vessel that brought the draenei to Azeroth.  Though stranded in the Azuremyst Isles, it acts as a home city for this noble race.', 'Le vaisseau naaru qui a conduit les draeneï en Azeroth. Bien que naufragé dans l\'archipel de Brume-azur, il sert de capitale à ce noble peuple.');
