INSERT INTO `factiondbc` VALUES ('40', '40', '2', '160', '528', '1101', '0', '0', '0', '0', '4000', '3100', '500', '-12000', '1', '1', '1', '3', '11', '1', '0.25', '7', '5', 'Orgrimmar', 'Orgrimmar', 'Founded by Thrall, this Horde capital city is home to many of the proud orc clans of Durotar.', 'Fondée par Thrall, cette capitale de la Horde abrite les nombreux et fiers clans orcs de Durotar.');
INSERT INTO `factiondbc` VALUES ('41', '41', '128', '34', '528', '1101', '0', '0', '0', '0', '4000', '3100', '500', '-12000', '1', '1', '1', '3', '11', '1', '0.25', '7', '5', 'Darkspear Trolls', 'Trolls Sombrelance', 'This tribe of exiled trolls has joined forces with Thrall and the Horde.  They now call Durotar their home, which they share with their orc allies.', 'Cette tribu de trolls exilés a joint ses forces à celles de Thrall et de la Horde. Ils partagent désormais Durotar avec leurs alliés orcs.');
INSERT INTO `factiondbc` VALUES ('42', '42', '32', '130', '528', '1101', '0', '0', '0', '0', '4000', '3100', '500', '-12000', '1', '1', '1', '3', '11', '1', '0.25', '7', '5', 'Thunder Bluff', 'Les Pitons du Tonnerre', 'Cairne Bloodhoof leads his people, the proud Tauren, from this mighty Horde capital.', 'Cairne Sabot-de-sang dirige son peuple, les fiers taurens, depuis cette puissante capitale de la Horde.');
INSERT INTO `factiondbc` VALUES ('43', '43', '16', '512', '162', '1101', '0', '0', '0', '0', '4000', '3100', '500', '-12000', '1', '1', '1', '3', '11', '1', '0.25', '7', '5', 'Undercity', 'Fossoyeuse', 'Led by Sylvanas Windrunner, the Forsaken have joined a tenuous alliance with the Horde and established this capital in the vast depths under the Ruins of Lordaeron.', 'Dirigés par Sylvanas Coursevent, les Réprouvés ont établi une alliance ténue avec la Horde et se sont installés dans les vastes catacombes qui s’étendent sous les ruines de Lordaeron.');
